import React, { useRef, useState } from "react";
import QRCode from "qrcode";

const QRform = ({ url, seturl }) => {
  const URLRef = useRef(null);
  const [qrcode, setqrcode] = useState("");
  const handleGenQR = (e) => {
    e.preventDefault();
    const urlval = URLRef.current.value.toString();
    seturl(urlval);

    URLRef.current.value = "";
    QRCode.toDataURL(url, (err, url) => {
      if (err) console.log(err);
      console.log(url);
      setqrcode(url);
    });
  };

  return (
    <div className="w-full m-3 flex justify-center align-center ">
      <form className="w-full">
        <input
          type="text"
          name=""
          id=""
          placeholder="Type in a url😁"
          className="py-2 w-96 p-2 border-collapse border border-blue-300 rounded-xl text-black m-4"
          ref={URLRef}
        />
        <button
          type="submit"
          className="m-4 px-4 py-2 font-bold bg-blue-500 rounded-xl text-white"
          onClick={handleGenQR}
        >
          Generate
        </button>
      </form>
      {qrcode && (
        <>
          <img src="{qrcode}" />
        </>
      )}
    </div>
  );
};

export default QRform;
